import React, {useState , useEffect} from "react"
import axios from "axios";


function Signup(){

const [email,setEmail] = useState("");
const [password,setPassword] = useState("");
const [repassword,setRepassword] = useState("");
const [name,setName] = useState("");


      function Senddata(e){
        e.preventDefault();
        const user={
          email,
          password,
          name
        }

     
        //conecting to backend
        axios.post("http://localhost:8070/users/add",user).then(()=>{
         alert("sucess!")
        }).catch((err)=>{
         alert(err)
        })
        }


        function myfunction()
        {   
            var emailInput=document.getElementById("Email1").value
            var mailformat = (/^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/);
            var submitButton = document.getElementById("submit");
           
            if(emailInput.match(mailformat))
            {
              document.getElementById("emailError").innerHTML = "";
              submitButton.disabled = false;
             
            }
            else
            {
              if(emailInput===''){
                document.getElementById("emailError").innerHTML = "Can't be empty";
              }
              else{
                document.getElementById("emailError").innerHTML = "Invalid Email";
                // alert("invalid Email")
                submitButton.disabled = true;
              }
               
                
            }
        
        }

        function pwdcheck(){
          var pwd = document.getElementById("Password1").value;
          var repwd = document.getElementById("Password2").value;
          var submitButton = document.getElementById("submit");
        
          if(pwd.length>=8 && pwd===repwd){
            document.getElementById("pwdError").innerHTML = "";
            submitButton.disabled = false;
            return true;
          }
          else{
            submitButton.disabled = true;
            if(pwd.length<8){
              document.getElementById("pwdError").innerHTML = "Password must be at least 8 characters";
              submitButton.disabled = true;
            }
            else{
              document.getElementById("pwdError").innerHTML = "Passwords do not match";
              submitButton.disabled = true;
            }
            
           
            return false;
          }
        } 
        
        
        
        function showpwd1(){
          var pwd = document.getElementById("Password1");
        
          if(pwd.type === "Password1"){
            pwd.type = "text";
          }
          else{
            pwd.type = "Password1"
          }
        }
        
        
        function showpwd2(){
          var pwd = document.getElementById("Password2");
        
          if(pwd.type === "Password2"){
            pwd.type = "text";
          }
          else{
            pwd.type = "Password2"
          }
        }
        
        
        const YourComponent = () => {
          const [newpwd, setNewpwd] = useState('');
          const [pwdError, setPwdError] = useState('');
          const [submitButtonDisabled, setSubmitButtonDisabled] = useState(true);
        
          useEffect(() => {
            // Check password length when the component mounts or when newpwd changes
            if (newpwd.length < 8) {
              setPwdError("Password must be at least 8 characters");
              setSubmitButtonDisabled(true);
            } else {
              setPwdError('');
              setSubmitButtonDisabled(false);
            }
          }, [newpwd]);
        
          const handlePasswordChange = (e) => {
            // Update the newpwd state when the password input changes
            setNewpwd(e.target.value);
          };
        }

return(
  <div className="container  container d-flex align-items-center justify-content-center vh-100">
    <img src="logo.jpg" alt="logo" style={{width: "200px", position:"absolute", top:"4.5vh"}}></img>
    <div className="container-fluid bg-secondary w-50 pt-4 rounded d-flex justify-content-center align-items-center">
        <form action ="" method="post" style={{width:"25vw"}} onSubmit={Senddata}>
        <div className="mb-3">
          <label for="Email1" className="form-label">Email address</label>
          <input type="email" className="form-control" id="Email1" name="email" required onChange={(e)=>{setEmail(e.target.value);}}></input>
          <span id="emailError" style={{color: "red"}}></span>

          <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div className="mb-3">
          <label for="Password1" className="form-label">Password</label>
          <div className="row" style={{width: "32vw"}}>
            <div className="col-10">
            <input type="password" className="form-control" id="Password1" onClick={myfunction} name="pwd" value={newpwd}  onChange={handlePasswordChange} required onChange={(e)=>{setPassword(e.target.value);}}></input>
            <div className="form-text">(Minumum-8 characters)</div>
            </div>
            <div className="col mt-2">
            <script src="https://cdn.lordicon.com/lordicon.js"></script>
            <lord-icon
                src="https://cdn.lordicon.com/fmjvulyw.json"
                trigger="click"
                id="showpwd" onClick={showpwd1}
                style={{width:"25px" , height:"25px" }}>
                
            </lord-icon>
            </div>
          </div>
        </div>

        <div className="mb-4">
          <label for="Password2" className="form-label">Repeat Password</label>
          <div className="row"  style={{width:"32vw"}}>
            <div className="col-10">
            <input type="password" className="form-control" id="Password2" name="repwd" required onChange={(e)=>{setRepassword(e.target.value);}}></input>
            <span id="pwdError" style={{color: "red"}}></span>
            </div>
            <div className="col mt-2">
            <script src="https://cdn.lordicon.com/lordicon.js"></script>
            <lord-icon
                src="https://cdn.lordicon.com/fmjvulyw.json"
                trigger="click"
                id="showpwd"
                style={{width:"25px" , height:"25px" }}
                onClick={showpwd2}>
            </lord-icon>
            </div>
          </div>
        </div>
        <div className="">
          <label className="form-check-label" for="name">Name</label>
          <input type="nickname" className="form-control mb-4" id="name1" onClick={() => { myfunction(); pwdcheck(); }} name="name"  required onChange={(e)=>{setName(e.target.value);}}></input>
        </div>
        <div className="d-flex align-items-center justify-content-center mb-4">
        <button type="submit" className="btn btn-primary " id="submit" onClick={() => { myfunction(); pwdcheck(); }}  >Signup</button>
        </div>
      </form>
    </div>
</div>


    )
}

export default Signup;
