import React from "react";
import './App.css';

import Home from './Pages/Home';
import Signup from './Pages/signup';
import Login from './components/Login';
import Resetpwd from './Pages/ResetPwd'
import Header from './components/Header';
import Footer from './components/Footer';
import {Route, Routes, BrowserRouter,useLocation} from "react-router-dom";

function App() {
  // Get the current location
  const location = useLocation();

  // Define an array of routes where the header should be hidden
  const hiddenHeaderRoutes = ['/Signup', '/Login'];

  // Check if the current location matches any of the routes where the header should be hidden
  const hideHeader = hiddenHeaderRoutes.some(route => location.pathname.includes(route));
  return (
    <div className="App">
    <BrowserRouter>
    {!hideHeader && <Header />}
      <Routes>
        <Route path="" element={<Home />} />
        <Route path="/Signup" element={<Signup />} />
        <Route path="/Login" element={<Login/>}/>
        <Route path="/Reset" element={<Resetpwd/>}/>
      </Routes>
      <Footer />
    </BrowserRouter>
    </div>
  );
}

export default App;
