import React, { useState } from 'react';
import { CSSTransition } from 'react-transition-group';

const ZoomCard = ({ imageSrc, title, text }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="card text-bg-dark w-50 mt-4"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="position-relative overflow-hidden d-flex">
        <img
          src={imageSrc}
          className={`card-img ${isHovered ? 'zoom' : ''}`}
          alt="..."
          style={{ width: '50vw', transition: 'transform 0.3s' }}
        />
        <div className="card-img-overlay d-flex flex-column justify-content-center align-items-center">
          <h5 className="card-title">{title}</h5>
          <p className="card-text">{text}</p>
          <a href="#" className="text-white fw-bold stretched-link">
            View
          </a>
        </div>
      </div>
    </div>
  );
};

const YourComponent = () => {
  return (
    <div>
      <ZoomCard
        imageSrc="kal.jpg"
        title="Card title 1"
        text="This is a wider card with supporting text for image 1."
      />
      <ZoomCard
        imageSrc="katha.jpg"
        title="Card title 2"
        text="This is a wider card with supporting text for image 2."
      />
      {/* Add more instances of ZoomCard as needed */}
    </div>
  );
};

export default YourComponent;
