import React from 'react';
import { useParams } from 'react-router-dom';
import { usePwdReset } from '../hooks/usePwdReset';

const PasswordReset = () => {
  const { token } = useParams();
  const {password,repassword,setPassword,setRepassword,isLoading,error,resetPassword,status} = usePwdReset();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await resetPassword(token);
  };

  return (
    <div className="container  container d-flex align-items-center justify-content-center vh-100">
      <h2>Password Reset</h2>
      <div className="container bg-body-tertiary w-50 pt-4 rounded">
      <form onSubmit={handleSubmit}>
        <label htmlFor="password">New Password:</label>
        <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)}/>

        <label htmlFor="repassword">Confirm Password:</label>
        <input type="password" id="repassword" value={repassword} onChange={(e) => setRepassword(e.target.value)}/>

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Loading...' : 'Reset Password'}
        </button>

        {error && <p style={{ color: 'red' }}>{error}</p>}
        {status && <div className="error bg-primary my-2" style={{color:"white"}}>{status}</div>}
      </form>
      </div>
    </div>
  );
};

export default PasswordReset;
