import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode'; // Import jwtDecode instead of { jwtDecode }

export const useUpdate = () => {
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const navigation = useNavigate();

  const Update = async (password, newpassword) => {
    setIsLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found');
      }

      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId; // Assuming 'userId' is the key in the JWT payload
      console.log(userId);

      const response = await fetch(`http://localhost:4000/user/update/${userId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password, newpassword }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error);
      }

      // Password updated successfully
      setIsLoading(false);
      navigation('/');
    } catch (error) {
      setError(error.message);
      setIsLoading(false);
    }
  };

  return { Update, isLoading, error };
};
