import React, { useState } from 'react';
import { useLogout } from '../hooks/useLogout'
import { useAuthContext } from '../hooks/useAuthContext'
import { useUpdate } from '../hooks/useUpdate';

const Header = () => {
  const {logout}=useLogout()
  const {user}=useAuthContext()


   const [password, setPassword] = useState('')
   const [newpassword, setNewPassword] = useState('')
  
   const {Update ,error,status,isLoading} = useUpdate()
   
  
    const handleSubmit = async (e) => {
      e.preventDefault();

      var userEmail = localStorage.getItem('email');
      if (userEmail) {
        // Use the email as needed, for example:
        console.log('User email:', userEmail);
      } else {
        console.log('Email not found in localStorage');
      }

      await Update(password,newpassword)    
      
    }

    function resetfeilds(){
      var pwd = document.getElementById("Password1");
      var newpwd = document.getElementById("Password2");

      pwd.value = null;
      newpwd.value =null;
    }

  const handlelogout = ()=>{
    logout()
  }


  function showpwd1(){
    var pwd = document.getElementById("Password1");
  
    if(pwd.type === "Password1"){
      pwd.type = "text";
    }
    else{
      pwd.type = "Password1"
    }
  }


  function showpwd2(){
    var pwd = document.getElementById("Password2");
  
    if(pwd.type === "Password2"){
      pwd.type = "text";
    }
    else{
      pwd.type = "Password2"
    }
  }
  
  
  // check new password for 8 characters
  document.addEventListener("DOMContentLoaded", function () {
      var passwordInput = document.getElementById("Password2");
      var submitButton = document.getElementById("submit");
      var pwdError = document.getElementById("pwdError");

      passwordInput.addEventListener("input", function () {
          var newpwd = passwordInput.value;

          if (newpwd.length < 8) {
              pwdError.innerHTML = "New Password must be at least 8 characters";
              submitButton.disabled = true;
          } else {
              pwdError.innerHTML = "";
              submitButton.disabled = false;
          }
      });
  });

    return(
      <div >
        <nav className="navbar navbar-expand-lg bg-body-tertiary border border border-primary">
          <div className="container-fluid">
          <img src="logo.jpg" style={{width:"130px"}}></img>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">Home</a>
              </li>
             
              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Reservation
                </a>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#">Accomadation</a></li>
                  <li><a className="dropdown-item" href="#">Dine-in</a></li>
                  <li><a className="dropdown-item" href="#">Something else here</a></li>
                </ul>
              </li>

              <li className="nav-item">
                <a className="nav-link" href="#">Link</a>
              </li>

              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Packages & offers
                </a>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#">Accomadation</a></li>
                  <li><a className="dropdown-item" href="#">Dine-in</a></li>
      
                  <li><a className="dropdown-item" href="#">Something else here</a></li>
                </ul>
              </li>
              
            </ul>

           {user &&(
            <div>
              <a data-bs-toggle="offcanvas" href="#offcanvas" role="button" aria-controls="offcanvasExample" class="btn btn-outline-primary mt-2 ms-5 me-3">Profile</a>
              <a href ="" id="userStatus" className="btn btn-secondary ms-4 mt-2" onClick={handlelogout}>Logout</a>
            </div>
            )}
            {!user &&(
            <div>
            <a href ="/Login" id="userStatus" className="btn btn-secondary ms-4 mt-2">Sign in</a>
            </div>
            )}
            </div>
         </div>
        </nav>




        <div className="offcanvas offcanvas-start" tabindex="-1" id="offcanvas" aria-labelledby="offcanvasLabel" >
             <div className="offcanvas-header">
               <h4 className="offcanvas-title" id="offcanvasLabel">Profile</h4>
               <button type="button" className="btn-close" data-bs-dismiss="offcanvas" aria-label="Close" onClick={resetfeilds}></button>
             </div>
               <div className="offcanvas-body">
                 <div className="container w-100 h-100 bg-body-tertiary pt-4 ">
                 {user &&(
                  <div>
                 <h6>Email<span class="ms-4">{user.email}</span></h6>
                 <h6>Email<span class="ms-4">{user.name}</span></h6>
                 </div>
                 )}
                 <form onSubmit={handleSubmit} method="post" className="mt-4">
                     <h5 className="mb-4 ps-1">Change Password</h5>
                     <div className="row ps-2 bg-secondary">
                       <input type="password" placeholder="Current Password" id="Password1" name="Password1" className="w-75 col-9" onChange={(e)=>{setPassword(e.target.value);}}></input>
                       <div className="btn col" onClick={showpwd1}>Show</div>
                     </div><br/>
                     <div className="row ps-2 bg-secondary">
                       <input type="password" placeholder="New Password" id="Password2" name="Password2" className=" w-75 col-9" onChange={(e)=>{setNewPassword(e.target.value);}}></input>
                       <div className="btn col" onClick={showpwd2}>Show</div>
                     </div>

                     <span id="pwdError" style={{color: "red"}}></span>
                     <button className="btn btn-outline-primary mt-4" name="submit" id="submit" disabled={isLoading}>Change</button>
                     {error && <div className="error bg-danger my-2" style={{color:"white"}}>{error}</div>}
                     {status && <div className="error bg-primary my-2" style={{color:"white"}}>{status}</div>}
                  </form>
                    <div style={{position: "relative", top: "60px"}}>
                      <h5 className="ps-1">Delete account</h5>
                      <div type="submit" className="btn btn-danger mt-3" id="submit" data-bs-toggle="modal" data-bs-target="#Modal">Delete My Account</div>
                    </div>
               
                  </div>
                </div>
           </div>
        

 
               {/* model  */}
              <div className="modal fade" id="Modal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h1 className="modal-title fs-5" id="exampleModalLabel">Are You Sure?</h1>
                      <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                      This is Permenent. You can not recover account after deletion.
                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                     
                       <form action="" method="post">
                        
                        <button type="submit" className="btn btn-outline-danger">DELETE</button>
                       </form>
                    
                    </div>
                  </div>
                </div>
              </div>
      </div>
    )}
                 
 export default Header;
