// In useUpdate hook
import { useState } from 'react'
import { Navigate, useNavigate } from "react-router-dom";
import {jwtDecode} from "jwt-decode"


export const useUpdate = () => {
  const [error, setError] = useState(null);
  const [status, setStatus] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigation = useNavigate();
  

  const Update = async (password, newpassword) => {
    setIsLoading(true);
    setError(null);

    const token = localStorage.getItem('user');
    const email = token.email;
    console.log(email)

//     if (token) {
//     const decodedToken = jwtDecode(token);
//     const id = decodedToken.email; // Assuming 'email' is the key in the JWT payload
//     console.log(id)
// }


    try {

      const response = await fetch(`http://localhost:4000/user/update/65bf991263326858f02d03b8`,{
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password, newpassword }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error);
      }

      // Password updated successfully
      setIsLoading(false);
      setStatus(data.status);
      window.location.reload();
 
    } catch (error) {
      setError(error.message);
      setIsLoading(false);
      
    }
  };

  return { Update, isLoading, error,status };
};
