import React, {useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login(){

      const [email,setEmail] = useState("");
      const [password,setPassword] = useState("");
      const navigation = useNavigate();

        function login(e){
        e.preventDefault();
        const user={
          email,
          password
        }

     
        //conecting to backend
        axios.post("http://localhost:8070/users/login",user).then(()=>{
         alert("sucess!")
         navigation('/Header')
        }).catch((err)=>{
         alert(err)
        })
        }

    return(
    <div className="container  container d-flex align-items-center justify-content-center vh-100">
         <img src="logo.jpg" alt="logo" style={{width: "200px", position:"absolute", top:"12vh"}}></img>
        <div className="container bg-body-tertiary w-50 pt-4 rounded">
          <form action="" onSubmit={login} method="post">
           <div className="mb-3">
             <label for="exampleInputEmail1" className="form-label">Email address</label>
             <input type="email" className="form-control" id="exampleInputEmail1" name = "email" required onChange={(e)=>{setEmail(e.target.value);}}></input>
   
           </div>
           <div className="mb-2">
             <label for="exampleInputPassword1" className="form-label">Password</label>
             <input type="password" className="form-control" name = "pwd" id="Password1" required onChange={(e)=>{setPassword(e.target.value);}}></input>
           </div>
           
             <div className="forgot d-flex justify-content-end me-2 mb-3 ">
             <a href="" className="text-decoration-none">Forgot Password?</a>
           </div>
           <div className="d-flex align-items-center justify-content-center mb-2">
           <button type="submit" className="btn btn-primary w-25">Login</button>
           </div>
          </form>
          <div className="new d-flex justify-content-center">
          <p>Don't have an account?</p>
          <a href="/Signup" className="ms-2 text-decoration-none">Create</a>
          </div>
       </div>
    </div>
    )
    }


export default Login;
